import React, { Component } from 'react';
import CardList from './CardList.js';
import SearchBox from './SearchBox.js';
import {robots} from './robots.js';

const state = {
	robots: robots,
	searchfield: ''
}

class App extends Component {
	constructor() {
		this.state = {
			robots: robots,
			searchfield: ''
		}
	}
	render() {
		return (
			<div className='tc'>
				<h1>RoboFriends</h1>
				<SearchBox />
				<CardList robots={robots}/>
			</div>
		);
	}
}

export default App;
